function errorReport(msg, url, lineNo, columnNo, error) {
  /*eslint-disable */
  if (error) {
    console.error(error);
  }
  const reportItem = [`msg: ${msg}`, `url: ${url}`];
  if (lineNo) {
    reportItem.push(`lineNo: ${lineNo}`);
  }
  if (columnNo) {
    reportItem.push(`columnNo: ${columnNo}`);
  }
  console.error(`${reportItem.join(',')}`);
  /*eslint-disable */
}
function httpErrorReport(error, req, res) {
  /*eslint-disable */
  console.error(error);
  console.error('req', req);
  console.error('res', res);
  /*eslint-disable */
}

/**
 * ErrorHandler - Global error handler
 *
 * @param  {string | error} msg         If string, then deal as Uncaught Error.
 * If error, then deal as http(ajax) error
 * @param  {string | http request} urlOrReq    If Uncaught Error, then error
 * script url. If http error, then http request
 * @param  {string | http response} lineNoOrRes If Uncaught Error, then error
 * line number. If http error, then http response
 * @param  {string} columnNo    error column number, may not provide
 * by some browsers
 * @param  {error} error       error object, may not provide by some browsers
 * @return {bool}             skip browser error handler
 */
export default function ErrorHandler(
  msg, urlOrReq, lineNoOrRes, columnNo, error
) {
  if (typeof msg === 'string') {
    errorReport(msg, urlOrReq, lineNoOrRes, columnNo, error);
  } else {
    httpErrorReport(msg, urlOrReq, lineNoOrRes);
  }

  return false;
}

import shallowEqual from './utilities/shallowEqual';

class State {

  _state = {
    listLoading: false,
    list: [],
    item: null,
  }
  _listeners = []
  _broadcast() {
    this._listeners.forEach(listener => {
      listener.call();
    });
  }

  /**
   * get - get the entire app state
   *
   * @return {object}  { list: [], item: {}, listLoading: bool }
   */
  get() {
    return this._state;
  }

  /**
   * setListLoading - change the list loading status
   *
   * @param  {bool} listLoading indicate ajax is loading or not
   * @return {self}             State instance itself, to enable chain
   */
  setListLoading(listLoading) {
    const preState = this._state;
    this._state = { ...this._state, listLoading };
    if (!shallowEqual(preState, this._state)) {
      this._broadcast();
    }
    return this;
  }

  /**
   * setList - set the list to display
   *
   * @param  {array<news item>} list the list of news which will be display
   * @return {self}             State instance itself, to enable chain
   */
  setList(list) {
    const preState = this._state;
    this._state = { ...this._state, list };
    if (!shallowEqual(preState, this._state)) {
      this._broadcast();
    }
    return this;
  }

  /**
   * setItem - set the selected news item to display
   *
   * @param  {obj} item the selected news item which will be display
   * @return {self}             State instance itself, to enable chain
   */
  setItem(item) {
    const preState = this._state;
    this._state = { ...this._state, item };
    if (!shallowEqual(preState, this._state)) {
      this._broadcast();
    }
    return this;
  }

  /**
   * onChange - subscribe change event of state
   *
   * @param  {func} listener callback handler, will be called when state chagned
   * @return {self}             State instance itself, to enable chain
   */
  onChange(listener) {
    this._listeners.push(listener);
    return this;
  }
}

export default State;

