/**
 * componentRender - render the component
 *
 * @param  {class} Component the component class
 * @param  {obj} props     properties
 * @return {dom obj}           a dom wrapper
 */
export default function componentRender(Component, props) {
  const _comp = new Component(props);
  return _comp.render();
}

class Dom {
  constructor(tag) {
    if (typeof tag === 'string') {
      this.node = document.createElement(tag);
    } else {
      this.node = tag;
    }
  }
  empty() {
    while (this.node.firstChild) {
      this.node.removeChild(this.node.firstChild);
    }
    return this;
  }
  append(dom) {
    this.node.appendChild(dom.node);
    return this;
  }
  setClass(classNames) {
    this.node.className = classNames;
    return this;
  }
  setAttr(key, val) {
    this.node.setAttribute(key, val);
    return this;
  }
  innerHTML(inner) {
    this.node.innerHTML = inner;
    return this;
  }
  on(event, handler) {
    this.node.addEventListener(event, handler);
    return this;
  }
}


/**
 * createDom - create a dom wrapper with a given html tag
 *
 * @param  {string} tag html tag name
 * @return {dom wrapper}     dom wrapper
 */
export function createDom(tag) {
  return new Dom(tag);
}

/**
 * getDom - find a dom by id and creat wrapper
 *
 * @param  {string} id html dom id
 * @return {dom wrapper}     dom wrapper
 */
export function getDom(id) {
  return new Dom(document.getElementById(id));
}

/**
 * wrapDom - wrap an exist dom into dom wrapper
 *
 * @param  {dom} node an exist dom node
 * @return {dom wrapper}     dom wrapper
 */
export function wrapDom(node) {
  return createDom(node);
}

function allKeyDataSame(objA, objB, keys) {
  // Test for A's keys different from B.
  for (let i = 0; i < keys.length; i++) {
    const key = keys[i];
    if (!hasOwnProperty.call(objB, key) || objA[key] !== objB[key]) {
      return false;
    }
  }
  return true;
}
function allKeySame(objA, objB) {
  const keysA = Object.keys(objA);
  const keysB = Object.keys(objB);

  if (keysA.length !== keysB.length) {
    return false;
  }

  return allKeyDataSame(objA, objB, keysA);
}

/**
 * shallowEqual - compare to value same or not, only do shallow compare for obj
 *
 * @param  {obj} objA first value
 * @param  {obj} objB second value
 * @return {bool}      equal or not
 */
export default function shallowEqual(objA, objB) {
  return objA === objB || allKeySame(objA, objB);
}
